(102, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2023-12-07 05:33:15', NULL, 0, 'plain', '', 0, 0, 'info'),
(103, 'wc-admin-manage-orders-on-the-go', 'info', 'en_US', 'Manage your orders on the go', 'Look for orders, customer info, and process refunds in one click with the Woo app.', '{}', 'unactioned', 'woocommerce-admin', '2023-12-11 04:12:22', NULL, 0, 'plain', '', 0, 0, 'info'),
(104, 'amazon_pay_reactivate_q1-24', 'marketing', 'en_US', 'Special offer: Activate 0% promo rate for Amazon Pay', 'For a limited time, Woo merchants who register with Amazon Pay can enjoy a promotional rate of 0% on all transaction fees for 30 days. Valid for businesses based in the United Kingdom. Terms and conditions apply.', '{}', 'pending', 'woocommerce.com', '2024-01-10 04:06:54', NULL, 0, 'plain', '', 0, 0, 'info'),
(105, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2024-01-19 06:03:16', NULL, 0, 'plain', '', 0, 0, 'info'),
(106, 'custom_order_attribution_inbox_q1_2024', 'marketing', 'en_US', 'NEW: Custom Order Attribution', 'Order attribution tracking is now available for all Woo stores! See whether an order came from a search ad, video, or email. Make better marketing decisions and watch your products go from “maybe” to “must-have.”', '{}', 'pending', 'woocommerce.com', '2024-02-06 04:59:09', NULL, 0, 'plain', '', 0, 0, 'info'),
(107, 'wc_admin_UKpricingchange_WCPay_Q1_2024', 'marketing', 'en_US', 'Fee Changes for WooPayments in the EEA and UK', 'WooPayments fees for transactions between the EEA and UK will change as of 1st March, 2024. Transactions between the UK and EEA are now deemed by card networks to be cross-border, thus international payment fees will be applicable. This also changes base processing fees.', '{}', 'pending', 'woocommerce.com', '2024-03-01 04:28:22', NULL, 0, 'plain', '', 0, 0, 'info'),
(108, 'airwallex_q1_2024', 'marketing', 'en_US', 'Save big on foreign exchange (FX) fees', 'Boost your international sales by offering Apple Pay, Google Pay, and 60+ local payment methods with Airwallex. Save time and money by settling payments directly in USD, EUR, HKD, SGD, AUD, and more — without the extra FX fees. Simple, cost-effective, fast.', '{}', 'pending', 'woocommerce.com', '2024-03-01 04:28:22', NULL, 0, 'plain', '', 0, 0, 'info'),
(109, 'wc-admin-performance-on-mobile', 'info', 'en_US', 'Track your store performance on mobile', 'Monitor your sales and high performing products with the Woo app.', '{}', 'unactioned', 'woocommerce-admin', '2024-03-10 05:18:11', NULL, 0, 'plain', '', 0, 0, 'info');
